package org.algorithm.아아;import java.util.Scanner;public class 멘토링 {    static Scanner sc = new Scanner(System.in);    public static void main(String[] args) {        int n = sc.nextInt();        int m = sc.nextInt();        int[][] arr = new int[m][n];        for (int i = 0; i < m; i++) {            for (int j = 0; j < n; j++) {                arr[i][j] = sc.nextInt();            }        }        int result = solution(n, m, arr);        System.out.println(result);    }    static int solution(int n, int m, int[][] arr) {        int result = 0;        for (int i = 1; i <= n; i++) {            for (int j = 1; j <= n; j++) {                int tmp = 0;                for (int k = 0; k < m; k++) {                    int pi = 0, pj = 0;                    for (int s = 0; s < n; s++) {                        if (arr[k][s] == i) pi = s;                        if (arr[k][s] == j) pj = s;                    }                    if (pi < pj) {                        tmp++;                    }                }                if (tmp == m) result++;            }        }        return result;    }}